import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Image from "react-bootstrap/Image";
import logo from "./logo.svg";
import "./App.css";

function App() {
  const titles = [
    ["作品１", "img", "description"],
    ["作品２", "img", "description"],
  ];
  let titleList = [];
  titles.forEach((title, img, description, index) => {
    titleList.push(
      <Col sm={12} md={4} lg={4} xl={4} xxl={3} className="border">
        <Image></Image>
        <h3>{title}</h3>
      </Col>
    );
  });

  return (
    <Container fluid>
      <div>
        {titles.map((items, index) => {
          return (
            <ol>
              {items.map((subItems, sIndex) => {
                return <li> {subItems[0]} </li>;
                return <li> {subItems[1]} </li>;
                return <li> {subItems[2]} </li>;
              })}
            </ol>
          );
        })}
      </div>
      <Container className="text-center">
        <Row>
          <Col md={{ span: 6, offset: 3 }}>
            <h1 className="p-5">
              <Image
                src={logo}
                alt="osaka design office"
                className="img-fluid"
              ></Image>
            </h1>
          </Col>
        </Row>
      </Container>
      <Container className="text-left">
        <h2 className="text-center mb-5">Works</h2>
        <Row>{titleList}</Row>
      </Container>
    </Container>
  );
}

export default App;
