import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Image from "react-bootstrap/Image";
import logo from "./logo.svg";
import "./App.css";

function App() {
  const titles = [
    ["作品１", "img", "description"],
    ["作品２", "img", "description"],
  ];
  let titleList = [];
  titles.forEach((title, img, description, index) => {
    titleList.push(
      <Col sm={12} md={4} lg={4} xl={4} xxl={3} className="border">
        <Image></Image>
        <h3>{title}</h3>
      </Col>
    );
  });

  return (
    <Container fluid>
      <div>
        <table>
          <thead>
            <tr>
              {titles[0].map((item, index) => {
                return <th>{item}</th>;
              })}
            </tr>
          </thead>
          <tbody>
            {titles.slice(1, titles.length).map((item, index) => {
              return (
                <tr>
                  <td>{item[0]}</td>
                  <td>{item[1]}</td>
                  <td>{item[2]}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>

      <Container className="text-center">
        <Row>
          <Col md={{ span: 6, offset: 3 }}>
            <h1 className="p-5">
              <Image
                src={logo}
                alt="osaka design office"
                className="img-fluid"
              ></Image>
            </h1>
          </Col>
        </Row>
      </Container>
      <Container className="text-left">
        <h2 className="text-center mb-5">Works</h2>
        <Row>{titleList}</Row>
      </Container>
    </Container>
  );
}

export default App;
